import App from 'next/app';
import { ApolloProvider } from '@apollo/react-hooks';
import { ThemeProvider, CSSReset } from '@chakra-ui/core';
import Page from '../components/layout/Page';
import withData from '../lib/withData';
import PleaseSignIn from '../components/user/PleaseSignIn';
import theme from '../theme';

class MyApp extends App {
  static async getInitialProps({ Component, ctx, apolloClient }) {
    let pageProps = {};
    if (Component.getInitialProps) {
      pageProps = await Component.getInitialProps(ctx);
    }
    // this exposes the query to the user
    pageProps.query = ctx.query;

    return { pageProps, path: ctx.asPath };
  }

  render() {
    const { Component, apollo, pageProps, path } = this.props;
    function pathCheck(p) {
      if (p.startsWith('/reset')) {
        return <Component {...pageProps} />;
      }
      return (
        <ThemeProvider theme={theme}>
          <CSSReset />
          <PleaseSignIn>
            <Page
              shopsURL={
                p.startsWith('/shops') || (p.startsWith('/settings') && true)
              }
            >
              <Component {...pageProps} />
            </Page>
          </PleaseSignIn>
        </ThemeProvider>
      );
    }

    return (
      <>
        <style>{`
          .layout {
            display: flex;
          }
          .hover:hover {
            opacity: 0.85;
          }

          .shine {
            background: #e4e7eb;
            background-image: linear-gradient(
              to right,
              #e4e7eb 0%,
              #edeef1 20%,
              #e4e7eb 40%,
              #e4e7eb 100%
            );
            background-repeat: no-repeat;
            background-size: 800px 104px;
            display: inline-block;
            position: relative;

            -webkit-animation-duration: 1.5s;
            -webkit-animation-fill-mode: forwards;
            -webkit-animation-iteration-count: infinite;
            -webkit-animation-name: placeholderShimmer;
            -webkit-animation-timing-function: linear;
          }

          a:link {
            text-decoration: none;
            color: rgb(35, 67, 97);
          }

          a:visited {
            text-decoration: none;
            color: rgb(35, 67, 97);
          }

          a:hover {
            text-decoration: none;
            color: #2e669a;
          }

          a:active {
            text-decoration: none;
            color: #2e669a;
          }

          .clean-input {
            box-shadow: none;
          }

          @-webkit-keyframes placeholderShimmer {
            0% {
              background-position: -468px 0;
            }

            100% {
              background-position: 468px 0;
            }
          }

          @media screen and (max-width: 768px) {
            .hidden {
              display: none;
            }

            .layout {
              display: block;
            }
          }
          @media screen and (min-width: 769px) {
            .show {
              display: none;
            }
          }
          ::-webkit-input-placeholder {
            /* Chrome/Opera/Safari */
            color: #8b949c !important;
            opacity: 1 !important;
          }
          ::-moz-placeholder {
            /* Firefox 19+ */
            color: #8b949c !important;
            opacity: 1 !important;
          }
          :-ms-input-placeholder {
            /* IE 10+ */
            color: #8b949c !important;
            opacity: 1 !important;
          }
          :-moz-placeholder {
            /* Firefox 18- */
            color: #8b949c !important;
            opacity: 1 !important;
          }
          .dot-flashing {
            position: relative;
            width: 6px;
            height: 6px;
            border-radius: 5px;
            background-color: #d0d5da;
            color: #9880ff;
            animation: dotFlashing 1s infinite linear alternate;
            animation-delay: 0.5s;
          }

          .dot-flashing::before,
          .dot-flashing::after {
            content: "";
            display: inline-block;
            position: absolute;
            top: 0;
          }

          .dot-flashing::before {
            left: -12px;
            width: 6px;
            height: 6px;
            border-radius: 5px;
            background-color: #d0d5da;
            color: #9880ff;
            animation: dotFlashing 1s infinite alternate;
            animation-delay: 0s;
          }

          .dot-flashing::after {
            left: 12px;
            width: 6px;
            height: 6px;
            border-radius: 5px;
            background-color: #d0d5da;
            color: #9880ff;
            animation: dotFlashing 1s infinite alternate;
            animation-delay: 1s;
          }

          @keyframes dotFlashing {
            0% {
              background-color: #425a70;
            }
            50%,
            100% {
              background-color: #d0d5da;
            }
          }
        `}</style>
        <ApolloProvider client={apollo}>{pathCheck(path)}</ApolloProvider>
      </>
    );
  }
}

export default withData(MyApp);
