import React, { useState } from 'react';
import {
  Box,
  Drawer,
  DrawerOverlay,
  DrawerContent,
  DrawerCloseButton,
  Heading,
  Text,
  Button,
  DrawerBody,
  InputGroup,
  Input,
  InputLeftElement,
  InputRightElement,
  Icon,
} from '@chakra-ui/core';
import PropTypes from 'prop-types';
import { gql } from 'apollo-boost';
import { useQuery } from '@apollo/react-hooks';
import { ALL_SHOPS_QUERY, shopsQueryVars } from '../shops/ShopList';
import Logo from '../logo/Logo';
import Header from './Header';
import Meta from './Meta';

export const CHANNELS_QUERY = gql`
  query($first: Int!, $skip: Int!) {
    channels(orderBy: createdAt_ASC, first: $first, skip: $skip) {
      id
      type
      name
      createdAt
      settings
    }
  }
`;

export const channelsQueryVars = {
  skip: 0,
  first: 10,
};

const Page = ({ children, shopsURL }) => {
  const [isShown, setIsShown] = useState(false);

  const {
    data: channelsData,
    error: channelsError,
    loading: channelsLoading,
  } = useQuery(CHANNELS_QUERY, {
    variables: channelsQueryVars,
  });

  const {
    data: shopsData,
    error: shopsError,
    loading: shopsLoading,
  } = useQuery(ALL_SHOPS_QUERY, {
    variables: shopsQueryVars,
  });
  return (
    <>
      <Meta />
      <Box className="layout">
        {(() => {
          if (shopsError || !shopsData || !shopsData.shops) return null;
          const { shops } = shopsData;
          return (
            <>
              <Box className="hidden">
                <Box
                  position="sticky"
                  background="rgb(23, 43, 77)"
                  top="0"
                  display="grid"
                  boxShadow="0 0 1px rgba(67,90,111,.47), 0 2px 4px -2px rgba(67,90,111,.3)"
                  width="270px"
                >
                  <Header
                    channels={channelsData && channelsData.channels}
                    channelsError={channelsError}
                    shops={shopsData.shops}
                    shopsError={shopsError}
                    logo={
                      <Box margin={18} marginBottom={0}>
                        <Logo />
                      </Box>
                    }
                  />
                </Box>
              </Box>
              <Box className="show">
                <Drawer
                  size="sm"
                  isOpen={isShown}
                  placement="left"
                  onClose={() => setIsShown(false)}
                >
                  <DrawerOverlay />
                  <DrawerContent>
                    <DrawerCloseButton color="White" />
                    <DrawerBody bg="#f9f9fb" p={0}>
                      <Box>
                        <Header
                          channels={channelsData && channelsData.channels}
                          channelsError={channelsError}
                          shops={shopsData.shops}
                          shopsError={shopsError}
                          isShown={isShown}
                          onClick={() => setIsShown(false)}
                        />
                      </Box>
                    </DrawerBody>
                  </DrawerContent>
                </Drawer>
                <Box display="flex" padding={4} background="rgb(23, 43, 77)">
                  <Box
                    marginLeft={2}
                    flex={1}
                    alignItems="center"
                    display="flex"
                    onClick={() => setIsShown(true)}
                  >
                    <Box className="hover" borderRadius={7}>
                      <svg
                        width={38}
                        height={38}
                        viewBox="0 0 24 24"
                        fill="none"
                        stroke="#fff"
                        strokeWidth={1.5}
                        strokeLinecap="round"
                        strokeLinejoin="bevel"
                      >
                        <line x1={3} y1={12} x2={21} y2={12} />
                        <line x1={3} y1={6} x2={13} y2={6} />
                        <line x1={10} y1={18} x2={21} y2={18} />
                      </svg>
                    </Box>
                  </Box>

                  <Box width="200px" marginRight={7}>
                    <Logo />
                  </Box>
                </Box>
              </Box>
              <Box
                padding="1.3rem"
                paddingTop="0rem"
                width="100%"
                background="rgb(245, 246, 247)"
                minHeight="100vh"
              >
                {shops.length || shopsURL ? (
                  <Box maxWidth="1100px" marginY="0px" marginX="auto">
                    {children}
                  </Box>
                ) : (
                  <Box
                    {...{
